$.fn.txtShadow=function(o){$(document).mousemove(function(t){var n={blur:"3px",color:"#333"};$.extend(n,o),$(".shadow").each(function(o){var e=t.clientX,s=t.clientY,c=6,i=6,p=$(".shadow").eq(o).position(),a=parseInt(p.left),u=parseInt(p.top);e>a&&(c=-c),s>u&&(i=-i),$(".shadow").eq(o).css("textShadow",c+"px "+i+"px "+n.blur+" "+n.color),$(".shadow").eq(o).on({mousedown:function(){$(this).css("fontSize","100px").css("textShadow","0px 0px 20px #333")},mouseup:function(){$(this).css("fontSize","64px")}})})})};